interceptor.saveaction-perrow:

	String val=rrequest.getStringAttribute("key"+mRowData.get("deptno"),"");
	if(val.equals("true"))
	{
		return super.doSavePerRow(rrequest,rbean,mRowData,mParamValues,editbean);
	}else if(val.equals("false"))
	{
		return WX_RETURNVAL_SKIP;
	}else
	{
		rrequest.getWResponse().getMessageCollector().confirm("key"+mRowData.get("deptno"),"是否保存"+mRowData.get("deptno")+"记录？");
		return WX_RETURNVAL_TERMINATE;
	}

